    </div>

    <div id="modal-overlay" class="modal-overlay">
        <div class="modal-content">
            <button class="modal-close" onclick="closeModal()">&times;</button>
            <h2 id="modal-title" style="font-family: 'Cinzel', serif; color: var(--accent-primary); margin-top:0;"></h2>
            <div id="modal-body" class="prop-list"></div>
        </div>
    </div>

    <div id="portrait-mode-modal" aria-hidden="true">
        <div class="portrait-modal-content" role="dialog" aria-modal="true">
            <div class="portrait-modal-header">
                <h3 id="ui-modal-title" class="portrait-modal-title"></h3>
                <button id="portrait-mode-close-btn" aria-label="Close">&times;</button>
            </div>
            <div class="portrait-modal-body">
                <div>
                    <button id="ui-btn-url" class="portrait-preset-btn" data-portrait-mode="url"></button>
                    <div id="ui-help-url" class="portrait-help"></div>
                </div>
                <div id="ui-hint-url" class="portrait-hint"></div>
            </div>
        </div>
    </div>

    <script>
        // === ÈÄöÁî®ËºîÂä©ÂáΩÊï∏ÔºàTuple-aware, Áµ±‰∏Ä World/Mainchar/FamiliarÔºâ ===

        // Localization Dictionary
        <StaticDataHolderImpl />

        const isTuple = (v) => Array.isArray(v) && v.length >= 2 && typeof v[1] === 'string';
        const tupleVal = (v) => isTuple(v) ? v[0] : v;
        const tupleLabel = (v, fallback = '') => isTuple(v) ? (v[1] ?? fallback) : fallback;

        function getAt(obj, path, defaultValue = undefined) {
            if (!obj || path === null || path === undefined || path === '') return defaultValue;
            const keys = Array.isArray(path) ? path : String(path).split('.');
            let cur = obj;
            for (const k of keys) {
                if (cur === null || cur === undefined || typeof cur !== 'object' || !(k in cur)) return defaultValue;
                cur = cur[k];
            }
            return cur;
        }

        // Back-compat alias (older code may still call this)
        function SafeGetValue(obj, path, defaultValue = "N/A") {
            const v = getAt(obj, path, undefined);
            return (v === undefined || v === null) ? defaultValue : v;
        }

        function getV(obj, path, defaultValue = "N/A") {
            const raw = getAt(obj, path, undefined);
            const v = tupleVal(raw);
            return (v === undefined || v === null) ? defaultValue : v;
        }

        function getL(obj, path, fallbackLabel = "") {
            const raw = getAt(obj, path, undefined);
            const l = tupleLabel(raw, fallbackLabel);
            return (l === undefined || l === null || l === '') ? (fallbackLabel ?? '') : l;
        }

        function formatValue(val) {
            if (val === null || val === undefined) return LOCALE.empty_none;
            if (isTuple(val)) return formatValue(tupleVal(val));
            if (Array.isArray(val)) {
                // Non-tuple arrays: show JSON string for safety
                return JSON.stringify(val);
            }
            if (typeof val === 'object') {
                if (val.description !== undefined) return formatValue(val.description);
                if (val.name !== undefined) return formatValue(val.name);
                return null; // signal "recurse"
            }
            return val;
        }

        function setText(id, value, suffix = '') {
            const el = document.getElementById(id);
            if (el) el.textContent = String(formatValue(value)) + suffix;
        }

        function setLabel(id, label, fallback = '') {
            const el = document.getElementById(id);
            if (!el) return;
            const txt = (label === undefined || label === null || label === '') ? (fallback ?? '') : label;
            if (txt) el.textContent = txt;
        }

        // Convenience: set label + value from tuple at the same time
        function setKV(labelId, valueId, obj, path, fallbackLabel = '', suffix = '') {
            setLabel(labelId, getL(obj, path, fallbackLabel), fallbackLabel);
            setText(valueId, getV(obj, path, ''), suffix);
        }

        function updateProgressBar(barId, valueId, current, max) {
            const bar = document.getElementById(barId);
            const valueEl = document.getElementById(valueId);
            if (valueEl) valueEl.textContent = `${current} / ${max}`;
            if (bar) {
                const percentage = max > 0 ? (current / max) * 100 : 0;
                bar.style.width = `${Math.max(0, Math.min(100, percentage))}%`;
            }
        }

        function openPage(evt, pageId) {
            document.querySelectorAll('.page').forEach(p => p.classList.remove('active-page'));
            document.querySelectorAll('.tab-btn').forEach(b => b.classList.remove('active'));
            document.getElementById(pageId).classList.add('active-page');
            evt.currentTarget.classList.add('active');
        }

        function closeModal() { document.getElementById('modal-overlay').style.display = 'none'; }


        // === Ê∏≤ÊüìÂáΩÊï∏ ===
        function renderQuestList(containerId, data, emptyText = LOCALE.empty_none) {
            const container = document.getElementById(containerId);
            if (!container) return;
            container.innerHTML = '';

            if (!data || Object.keys(data).length === 0) {
                container.innerHTML = `<div style="text-align:center; color:var(--text-secondary); padding:10px;">${emptyText}</div>`;
                return;
            }

            for (const [key, raw] of Object.entries(data)) {
                if (key === '$meta' || key === 'template') continue;

                const quest = (isTuple(raw) ? tupleVal(raw) : raw) || {};
                const div = document.createElement('div');
                div.className = 'quest-card';

                // Extract fields
                const title = key;
                const desc = quest.description || (typeof quest === 'string' ? quest : '');
                const reward = quest.reward || '';
                const difficulty = quest.difficulty || '';
                const status = (quest.status || 'Active').toLowerCase();

                // Status Color
                let statusClass = '';
                if (status === 'active') statusClass = 'quest-status-active';
                else if (status === 'completed') statusClass = 'quest-status-completed';
                else if (status === 'failed') statusClass = 'quest-status-failed';

                div.innerHTML = `
                    <div class="quest-header">
                        <span class="quest-title">${title}</span>
                        ${difficulty ? `<span class="quest-badge">${difficulty}</span>` : ''}
                    </div>
                    ${desc ? `<div class="quest-desc">${desc}</div>` : ''}
                    <div class="quest-footer">
                        ${reward ? `<span class="quest-reward">üèÜ ${reward}</span>` : '<span></span>'}
                        <span class="${statusClass}">${status.toUpperCase()}</span>
                    </div>
                `;

                div.onclick = () => showModal(title, raw);
                container.appendChild(div);
            }
        }

        function renderSimpleList(containerId, data, emptyText = LOCALE.empty_none) {
            const container = document.getElementById(containerId);
            if (!container) return;
            container.innerHTML = '';
            if (!data || Object.keys(data).length === 0) {
                container.innerHTML = `<div style="text-align:center; color:var(--text-secondary); padding:10px;">${emptyText}</div>`;
                return;
            }
            for (const [key, raw] of Object.entries(data)) {
                if (key === '$meta' || key === 'template') continue;
                const div = document.createElement('div');
                div.className = 'list-entry';

                // Tuple leaf: [value,label]
                if (isTuple(raw)) {
                    const title = tupleLabel(raw, key);
                    const desc = String(formatValue(tupleVal(raw)));
                    div.innerHTML = `<div class="entry-title">${title}</div>${desc ? `<div class="entry-desc">${desc}</div>` : ''}`;
                    div.onclick = () => showModal(title, raw);
                    container.appendChild(div);
                    continue;
                }

                let title = key;
                let desc = '';
                if (raw && typeof raw === 'object') {
                    if (raw.description !== undefined) desc = String(formatValue(raw.description));
                    else if (raw.type !== undefined) desc = String(formatValue(raw.type));
                } else if (typeof raw === 'string' || typeof raw === 'number' || typeof raw === 'boolean') {
                    desc = String(raw);
                }

                div.innerHTML = `<div class="entry-title">${title}</div>${desc ? `<div class="entry-desc">${desc}</div>` : ''}`;
                div.onclick = () => showModal(title, raw);
                container.appendChild(div);
            }
        }

        function renderGridList(containerId, data, emptyText = LOCALE.empty_null) {
            const container = document.getElementById(containerId);
            if (!container) return;
            container.innerHTML = '';

            function flatten(obj) {
                let items = [];
                if (!obj || typeof obj !== 'object') return items;
                for (const [key, value] of Object.entries(obj)) {
                    if (key === '$meta' || value === null || value === undefined) continue;
                    const unwrapped = isTuple(value) ? tupleVal(value) : value;
                    if (unwrapped === null || unwrapped === undefined) continue;
                    if (typeof unwrapped === 'object' && !Array.isArray(unwrapped)) {
                        if (unwrapped.type || unwrapped.description) items.push({ name: key, ...unwrapped });
                        else items = items.concat(flatten(unwrapped));
                    } else {
                        items.push({ name: isTuple(value) ? tupleLabel(value, key) : key, quantity: unwrapped });
                    }
                }
                return items;
            }

            const items = flatten(data);
            if (items.length === 0) { container.innerHTML = `<div style="grid-column:1/-1; text-align:center; color:var(--text-secondary);">${emptyText}</div>`; return; }
            items.forEach(item => {
                const div = document.createElement('div');
                div.className = 'grid-item';
                let desc = item.description || item.type || '';
                let qty = item.quantity;
                if (qty && typeof qty === 'number' && qty > 1) desc = `x${qty}`;
                else if (qty && typeof qty === 'number' && qty === 1 && !desc) desc = `x1`;
                div.innerHTML = `<div class="grid-item-name">${item.name}</div><div class="grid-item-desc">${desc}</div>`;
                div.onclick = () => showModal(item.name, item);
                container.appendChild(div);
            });
        }

        function renderRecursive(container, data) {
            // Leaf handling
            if (data === null || data === undefined) { container.textContent = LOCALE.empty_none; return; }
            if (isTuple(data)) { container.textContent = String(formatValue(tupleVal(data))); return; }
            if (Array.isArray(data)) { container.textContent = String(formatValue(data)); return; }
            if (typeof data !== 'object') { container.textContent = String(formatValue(data)); return; }

            for (const [key, value] of Object.entries(data)) {
                if (key === '$meta' || key === 'template' || key === 'name') continue;
                const wrapper = document.createElement('div');
                wrapper.style.marginBottom = '8px';

                if (isTuple(value)) {
                    const label = tupleLabel(value, key);
                    const v = tupleVal(value);
                    wrapper.className = 'prop-row';
                    wrapper.style.borderBottom = '1px dashed var(--border-color)';
                    wrapper.style.paddingBottom = '4px';
                    wrapper.innerHTML = `<span class="prop-label">${label}</span><span class="prop-val">${formatValue(v)}</span>`;
                    container.appendChild(wrapper);
                    continue;
                }

                let formatted = formatValue(value);
                if (formatted === null && typeof value === 'object') {
                    const title = document.createElement('div');
                    title.className = 'prop-label';
                    title.style.fontWeight = 'bold';
                    title.style.color = 'var(--accent-primary)';
                    title.style.marginBottom = '4px';
                    title.textContent = key;
                    wrapper.appendChild(title);
                    const subContainer = document.createElement('div');
                    subContainer.style.paddingLeft = '15px';
                    subContainer.style.borderLeft = '2px solid rgba(255,215,0,0.3)';
                    renderRecursive(subContainer, value);
                    wrapper.appendChild(subContainer);
                } else {
                    wrapper.className = 'prop-row';
                    wrapper.style.borderBottom = '1px dashed var(--border-color)';
                    wrapper.style.paddingBottom = '4px';
                    wrapper.innerHTML = `<span class="prop-label">${key}</span><span class="prop-val">${formatted}</span>`;
                }
                container.appendChild(wrapper);
            }
        }

        function showImageModal(title, url) {
            // Determine target document (parent window vs iframe)
            let targetDoc = document;
            try {
                if (typeof window.parent !== 'undefined' && window.parent.document) {
                    targetDoc = window.parent.document;
                }
            } catch (e) {
                console.warn("[MapDebug] Access to parent window blocked:", e);
            }

            // close any existing viewer in the target document
            const old = targetDoc.getElementById('img-viewer-overlay');
            if (old) old.remove();

            const overlay = targetDoc.createElement('div');
            overlay.id = 'img-viewer-overlay';
            overlay.style.cssText = `
                position: fixed; inset: 0; z-index: 200000;
                background: rgba(0,0,0,0.88); backdrop-filter: blur(6px);
                display: flex; align-items: center; justify-content: center;
                padding: 10px;
            `;

            // top bar
            const bar = targetDoc.createElement('div');
            bar.style.cssText = `
                position: fixed; top: 10px; left: 10px; right: 10px;
                display: flex; align-items: center; justify-content: space-between;
                gap: 10px; z-index: 200001; pointer-events: none;
                color: #E0C9A6; font-family: 'Cinzel', serif;
                text-shadow: 0 0 10px rgba(0,0,0,0.8);
            `;

            const titleEl = targetDoc.createElement('div');
            titleEl.textContent = title || '';
            titleEl.style.cssText = `font-size: 16px; pointer-events: auto;`;

            const closeBtn = targetDoc.createElement('button');
            closeBtn.innerHTML = '&times;';
            closeBtn.style.cssText = `
                pointer-events: auto;
                font-size: 34px; line-height: 1;
                background: none; border: none; cursor: pointer;
                color: #b0b0b0;
            `;
            closeBtn.onclick = () => overlay.remove();

            bar.appendChild(titleEl);
            bar.appendChild(closeBtn);

            // image
            const img = targetDoc.createElement('img');
            img.src = url;
            img.alt = title || 'image';
            img.style.cssText = `
                max-width: 98vw; max-height: 92vh;
                width: auto; height: auto;
                object-fit: contain;
                border-radius: 6px;
                border: 2px solid rgba(255,215,0,0.85);
                box-shadow: 0 0 18px rgba(0,0,0,0.85);
            `;

            // Click background to close
            overlay.addEventListener('click', (e) => { if (e.target === overlay) overlay.remove(); });

            // ESC to close
            const onKey = (e) => {
                if (e.key === 'Escape') {
                    overlay.remove();
                    targetDoc.removeEventListener('keydown', onKey);
                }
            };
            targetDoc.addEventListener('keydown', onKey);

            overlay.appendChild(img);
            overlay.appendChild(bar); // Put bar inside overlay for easier cleanup if structure allows, or append to body

            // Append to body
            targetDoc.body.appendChild(overlay);

            // Cleanup helper
            // Note: In original code bar was separate. Here I made bar child of overlay for simpler z-indexing,
            // or we keep them separate. Let's keep separate if we want bar fixed relative to viewport cleanly.
            // Actually, putting bar inside overlay is fine if overlay is fixed inset 0.
            // But let's follow the previous pattern if we want to be safe, but simply appending to body is easiest.
        }

        function showModal(title, data) {
            document.getElementById('modal-title').textContent = title;
            const body = document.getElementById('modal-body');
            body.innerHTML = '';
            renderRecursive(body, data);
            document.getElementById('modal-overlay').style.display = 'flex';
        }

        // === Familiar (Tuple-aware via getV/getL) ===
        // (Legacy wrappers removed)


        // JSON-safe string literal for inline handlers (avoids quote-breaking)
        // JS string literal safe for inline HTML handlers (attribute is double-quoted)
        // We return a *single-quoted* JS literal: '...'
        // (Backslash-escaping does NOT protect quotes in HTML attributes, so avoid double quotes.)
        const __jsStr = (v) => {
            const s = String(v ?? '');
            const escaped = s
                .replace(/\\/g, '\\\\')
                .replace(/'/g, "\\'")
                .replace(/\r/g, '\\r')
                .replace(/\n/g, '\\n')
                .replace(/\u2028/g, '\\u2028')
                .replace(/\u2029/g, '\\u2029');
            return `'${escaped}'`;
        };

        // HTML escape for user content
        const __escapeHtml = (s) => {
            const str = String(s ?? '');
            const map = { '&': '&amp;', '<': '&lt;', '>': '&gt;', '"': '&quot;', "'": "&#39;" };
            return str.replace(/[&<>"']/g, (c) => map[c] || c);
        };

        // Inline renderer for Familiar equipment list (shown at end of each Familiar profile)
        function renderKinEquipmentInline(char) {
            // Equipment can be a plain object, or a tuple where tupleVal is an object
            const raw = (char && char.Equipment !== undefined) ? char.Equipment : null;
            const equip = isTuple(raw) ? tupleVal(raw) : raw;

            if (!equip || typeof equip !== 'object' || Array.isArray(equip) || Object.keys(equip).length === 0) {
                return `<div class="kin-section-title">${LOCALE.header_kin_equip}</div><div class="kin-equip-empty">${LOCALE.lbl_kin_no_equip}</div>`;
            }

            const rows = Object.entries(equip).map(([name, v]) => {
                const vv = isTuple(v) ? tupleVal(v) : v;
                let desc = '';

                if (vv && typeof vv === 'object' && !Array.isArray(vv)) {
                    const d = vv.description ?? vv.desc ?? vv.Description ?? '';
                    desc = (d !== '' && d !== undefined && d !== null) ? d : JSON.stringify(vv);
                } else {
                    desc = vv;
                }

                return `
                <div class="kin-equip-row">
                    <span class="kin-equip-name">${__escapeHtml(name)}</span>
                    <span class="kin-equip-desc">${__escapeHtml(desc)}</span>
                </div>`;
            }).join('');

            return `<div class="kin-section-title">${LOCALE.header_kin_equip}</div><div class="kin-equip-list">${rows}</div>`;
        }

        function renderKinList(containerId, kinData) {
            try {
                const container = document.getElementById(containerId);
                if (!container) return;
                container.innerHTML = '';

                if (!kinData || Object.keys(kinData).length === 0) {
                    container.innerHTML = `<div style="text-align:center; color:var(--text-secondary); padding:20px;">${LOCALE.lbl_kin_no_info}</div>`;
                    return;
                }

                Object.entries(kinData).forEach(([kinKey, char]) => {
                    const card = document.createElement('div');
                    card.className = 'kin-card';
                    const imgUrl = getV(char, 'Image');

                    const kinKeyJs = __jsStr(kinKey);
                    const titleJs = __jsStr(getV(char, 'Name') ?? kinKey);
                    const urlJs = __jsStr(imgUrl ?? '');

                    const avatarInner = (imgUrl && imgUrl !== "N/A")
                        ? `<img src="${imgUrl}" class="kin-avatar-frame" alt="Avatar" style="cursor:pointer;" onclick="showImageModal(${titleJs}, ${urlJs})" title="${LOCALE.ui_click_enlarge}">`
                        : `<div class="kin-avatar-frame" style="display:flex;align-items:center;justify-content:center;color:#666;">(No Image)</div>`;

                    const avatarHtml = `
                    <div class="kin-avatar-wrap">
                        ${avatarInner}
                        <button class="kin-avatar-edit-btn" type="button" onclick="openPortraitModeModal(event, ${kinKeyJs})">edit</button>
                    </div>
                `;

                    const hpM = Number(getV(char, 'Hp_max')) || 100; const hpC = Math.min(Number(getV(char, 'Hp_curr')) || 0, hpM);
                    const mpM = Number(getV(char, 'Mp_max')) || 100; const mpC = Math.min(Number(getV(char, 'Mp_curr')) || 0, mpM);
                    const stM = Number(getV(char, 'Sta_max')) || 100; const stC = Math.min(Number(getV(char, 'Sta_curr')) || 0, stM);

                    const isInTeam = getV(char, 'Is_in_battle_team') === true;
                    const isPresent = getV(char, 'Is_present') === true;

                    // === NEW SPLIT ROW LAYOUT FOR STATS ===
                    const html = `
                    <div class="kin-image-col">
                        ${avatarHtml}
                        <div class="kin-tags">
                            <span class="kin-tag">${getV(char, 'Familiar_Status')}</span>
                            <span class="kin-tag">${getV(char, 'Occupation')}</span>
                        </div>
                        <div class="kin-checkbox-row">
                            <label class="kin-checkbox-label">
                                <input type="checkbox" ${isInTeam ? 'checked' : ''} onchange="toggleKinBattleTeam(event, ${kinKeyJs}, this.checked)"> 
                                ${LOCALE.lbl_kin_in_team}
                            </label>
                        </div>
                        <div class="kin-checkbox-row" style="margin-top:0;">
                            <label class="kin-checkbox-label">
                                <input type="checkbox" ${isPresent ? 'checked' : ''} disabled> 
                                ${LOCALE.lbl_kin_present}
                            </label>
                        </div>
                    </div>

                    <div class="kin-detail-col">
                        <div class="kin-header-row">
                            <span class="kin-subtext">${getV(char, 'Race')} Lv.${getV(char, 'Level')} - ${getV(char, 'Occupation')} (EXP: ${getV(char, 'Exp')}) ${Number(getV(char, 'Core-points')) > 0 ? ` (<span style="color:#f00;font-weight:bold;">${LOCALE.lbl_unused_pts}: ${getV(char, 'Core-points')}</span>)` : ''}</span>
                        </div>
						<div class="kin-stats-grid" style="grid-template-columns: repeat(2, 1fr);">
							<div class="kin-stat-box"><span class="kin-stat-val">${getV(char, 'Identity')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-val">${getL(char, 'Location', 'Location')}: ${getV(char, 'Location')}</span></div>
						</div>


                        <div class="kin-bar-group">
                            <div class="kin-bar-row">
                                <span class="kin-bar-label" style="color:#ff6b6b;">HP</span>
                                <div class="kin-bar-track"><div class="kin-bar-fill" style="width:${(hpC / hpM) * 100}%; background:var(--hp-fill);"></div></div>
                                <span>${hpC}/${hpM}</span>
                            </div>
                            <div class="kin-bar-row">
                                <span class="kin-bar-label" style="color:#4dabf7;">MP</span>
                                <div class="kin-bar-track"><div class="kin-bar-fill" style="width:${(mpC / mpM) * 100}%; background:var(--mp-fill);"></div></div>
                                <span>${mpC}/${mpM}</span>
                            </div>
                            <div class="kin-bar-row">
                                <span class="kin-bar-label" style="color:#ffd700;">STA</span>
                                <div class="kin-bar-track"><div class="kin-bar-fill" style="width:${(stC / stM) * 100}%; background:var(--sta-fill);"></div></div>
                                <span>${stC}/${stM}</span>
                            </div>
                        </div>

                        <div class="kin-section-title">${LOCALE.header_kin_stats}</div>

                        <div class="kin-stats-grid" style="grid-template-columns: repeat(5, 1fr);">
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Strength', 'Strength')}</span><span class="kin-stat-val">${getV(char, 'Strength')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Agility', 'Agility')}</span><span class="kin-stat-val">${getV(char, 'Agility')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Constitution', 'Constitution')}</span><span class="kin-stat-val">${getV(char, 'Constitution')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Intelligence', 'Intelligence')}</span><span class="kin-stat-val">${getV(char, 'Intelligence')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Wisdom', 'Wisdom')}</span><span class="kin-stat-val">${getV(char, 'Wisdom')}</span></div>

                        </div>

						<div class="kin-stats-grid" style="grid-template-columns: repeat(5, 1fr);">
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Physical_attack', 'P.Atk')}</span><span class="kin-stat-val">${getV(char, 'Physical_attack')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Physical_defense', 'P.Def')}</span><span class="kin-stat-val">${getV(char, 'Physical_defense')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Magic_attack', 'M.Atk')}</span><span class="kin-stat-val">${getV(char, 'Magic_attack')}</span></div>
                            <div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Magic_defense', 'M.Def')}</span><span class="kin-stat-val">${getV(char, 'Magic_defense')}</span></div>
							<div class="kin-stat-box"><span class="kin-stat-label">${getL(char, 'Magic_assist', 'M.Ast')}</span><span class="kin-stat-val">${getV(char, 'Magic_assist')}</span></div>
                        </div>

