        // === Avatar editing (MVU write-back) ===
        const __statusmenu_state = { gameData: null, stat: null, currentKinKey: null };

        function openPortraitModeModal(evt, kinKey) {
            try {
                if (evt) { evt.preventDefault(); evt.stopPropagation(); }
                __statusmenu_state.currentKinKey = kinKey;
                const modal = document.getElementById('portrait-mode-modal');
                if (modal) modal.style.display = 'flex';
