            const modal = document.getElementById('portrait-mode-modal');
            if (modal) modal.style.display = 'none';
            __statusmenu_state.currentKinKey = null;
        }

        function normalizePortraitUrl(raw) {
            const trimmed = String(raw ?? '').trim();
            if (!trimmed) return '';
            if (!/^https?:\/\//i.test(trimmed)) return '';
            return trimmed;
        }

        async function saveStatData(updatedStat) {
            if (!updatedStat) return;
            const messageId = (typeof getCurrentMessageId === 'function') ? getCurrentMessageId() : 'last';

            try {
                // Priority: MVU Extension (updateVariablesWith)
                if (typeof updateVariablesWith === 'function') {
                    await updateVariablesWith((vars) => {
                        if (typeof _ !== 'undefined' && _.set) {
                            _.set(vars, 'stat_data', updatedStat);
                        } else {
                            vars.stat_data = updatedStat;
                        }
                        return vars;
                    }, { type: 'message', message_id: messageId });
                }
                // Fallback: Standard SillyTavern API
                else if (typeof window.insertOrAssignVariables === 'function') {
                    const payload = { stat_data: updatedStat };
                    await window.insertOrAssignVariables(payload, { type: 'chat' });
                    await window.insertOrAssignVariables(payload, { type: 'message', message_id: messageId });
                } else {
                    alert(LOCALE.alert_no_func);
                    return;
                }

                __statusmenu_state.stat = updatedStat;
                if (__statusmenu_state.gameData) __statusmenu_state.gameData.stat_data = updatedStat;
                renderKinList('kin-container', updatedStat.Familiar || {});

            } catch (e) {
                console.error('saveStatData failed:', e);
                alert(LOCALE.alert_save_fail);
            }
        }

        async function applyPortraitUpdate(mode, rawInput) {
            const kinKey = __statusmenu_state.currentKinKey;
            if (!kinKey) return;
            const url = normalizePortraitUrl(rawInput);
            if (!url) { alert(LOCALE.alert_invalid_url); return; }
            const stat = __statusmenu_state.stat;
            if (!stat) { alert(LOCALE.alert_no_stat); return; }

            if (kinKey === '__MAINCHAR__') {
                // Handle Main Character
                stat.Mainchar = stat.Mainchar || {};
                const existing = stat.Mainchar.Image;
                const label = (Array.isArray(existing) && existing.length > 1) ? existing[1] : 'Portrait';
                stat.Mainchar.Image = [url, label];
            } else {
                stat.Familiar = stat.Familiar || {};
                stat.Familiar[kinKey] = stat.Familiar[kinKey] || {};
                const existing = stat.Familiar[kinKey].Image;
                const label = (Array.isArray(existing) && existing.length > 1) ? existing[1] : 'Avatar';
                stat.Familiar[kinKey].Image = [url, label];
            }

            await saveStatData(stat);
            closePortraitModeModal();
        }

        async function toggleKinBattleTeam(evt, kinKey, checked) {
            try {
                if (evt) { evt.preventDefault(); evt.stopPropagation(); }
                const stat = __statusmenu_state.stat;
                if (!stat || !kinKey) return;
                stat.Familiar = stat.Familiar || {};
                stat.Familiar[kinKey] = stat.Familiar[kinKey] || {};
                const existing = stat.Familiar[kinKey].Is_in_battle_team;
                const label = (Array.isArray(existing) && existing.length > 1) ? existing[1] : LOCALE.lbl_kin_in_team;
                stat.Familiar[kinKey].Is_in_battle_team = [!!checked, label];
                await saveStatData(stat);
            } catch (e) {
                console.error('toggleKinBattleTeam failed:', e);
                alert(LOCALE.alert_save_fail);
            }
        }

        // === ‰∏ªÊõ¥Êñ∞ÈÇèËºØ ===
        async function updateStatusPanel(gameData) {
            if (!gameData || !gameData.stat_data) return;
            const stat = gameData.stat_data;

            __statusmenu_state.gameData = gameData;
            __statusmenu_state.stat = stat;

            // ---- World (label comes from tuple[1]) ----
            setKV('lbl-world-date', 'world-date', stat, 'World.Date', 'Date');
            setKV('lbl-world-time', 'world-time', stat, 'World.Time', 'Time');
            setKV('lbl-world-location', 'world-location', stat, 'World.Location', 'Location');
            setKV('lbl-world-webther', 'world-weather', stat, 'World.Weather', 'Weather');

            // ---- Main Character Avatar ----
            const mcImg = document.getElementById('mc-avatar-img');
            if (mcImg) {
                const rawImg = getV(stat, 'Mainchar.Image');
                mcImg.src = (rawImg && rawImg !== "N/A") ? rawImg : "";
