            }
            setText('mc-name-display', getV(stat, 'Mainchar.Name', ''));

            // ---- Main Character Vitals ----
            // Labels for the vitals bar (read from tuple[1])
            setText('lbl-vital-hp', getL(stat, 'Mainchar.Hp_curr', 'HP'));
            setText('lbl-vital-mp', getL(stat, 'Mainchar.Mp_curr', 'MP'));
            setText('lbl-vital-sta', getL(stat, 'Mainchar.Sta_curr', 'Stamina'));

            const hp_max = Number(getV(stat, 'Mainchar.Hp_max', 100)) || 100;
            const hp_cur = Math.min(Number(getV(stat, 'Mainchar.Hp_curr', 0)) || 0, hp_max);
            updateProgressBar('hp-bar', 'hp-value', hp_cur, hp_max);

            const mp_max = Number(getV(stat, 'Mainchar.Mp_max', 100)) || 100;
            const mp_cur = Math.min(Number(getV(stat, 'Mainchar.Mp_curr', 0)) || 0, mp_max);
            updateProgressBar('mp-bar', 'mp-value', mp_cur, mp_max);

            const sta_max = Number(getV(stat, 'Mainchar.Sta_max', 100)) || 100;
            const sta_cur = Math.min(Number(getV(stat, 'Mainchar.Sta_curr', 0)) || 0, sta_max);
            updateProgressBar('sta-bar', 'sta-value', sta_cur, sta_max);

            // ---- Core (labels from tuples) ----
            setKV('lbl-core-name', 'core-name', stat, 'Mainchar.Name', 'Name');
            setKV('lbl-core-race', 'core-race', stat, 'Mainchar.Race', 'Race');
            setKV('lbl-core-occupation', 'core-occupation', stat, 'Mainchar.Occupation', 'Occupation');
            setKV('lbl-core-level', 'core-level', stat, 'Mainchar.Level', 'Level');
            setKV('lbl-core-exp', 'core-exp', stat, 'Mainchar.Exp', 'Exp');
            setKV('lbl-core-mental', 'core-mental', stat, 'Mainchar.Mental_state', 'Mental');
            setKV('lbl-core-points', 'core-points', stat, 'Mainchar.Core-points', 'Points');

            // ---- Attributes ----
            setKV('lbl-attr-strength', 'attr-strength', stat, 'Mainchar.Strength', 'Strength');
            setKV('lbl-attr-agility', 'attr-agility', stat, 'Mainchar.Agility', 'Agility');
            setKV('lbl-attr-constitution', 'attr-constitution', stat, 'Mainchar.Constitution', 'Constitution');
            setKV('lbl-attr-intelligence', 'attr-intelligence', stat, 'Mainchar.Intelligence', 'Intelligence');
            setKV('lbl-attr-wisdom', 'attr-wisdom', stat, 'Mainchar.Wisdom', 'Wisdom');
            setKV('lbl-attr-charisma', 'attr-charisma', stat, 'Mainchar.Charisma', 'Charisma');

            // ---- Derived combat stats ----
            setKV('lbl-der-p-dmg', 'der-p-dmg', stat, 'Mainchar.Physical_attack', 'P.Atk');
            setKV('lbl-der-p-def', 'der-p-def', stat, 'Mainchar.Physical_defense', 'P.Def');
            setKV('lbl-der-m-dmg', 'der-m-dmg', stat, 'Mainchar.Magic_attack', 'M.Atk');
            setKV('lbl-der-m-def', 'der-m-def', stat, 'Mainchar.Magic_defense', 'M.Def');
            setKV('lbl-der-m-assist', 'der-m-assist', stat, 'Mainchar.Magic_assist', 'M.Ast');

            // ---- Lists / grids (tuple-safe renderers) ----
            renderQuestList('quest-list', getAt(stat, 'Mainchar.Quests', {}), LOCALE.empty_quest);
            renderGridList('skills-list', getAt(stat, 'Mainchar.Skills', {}), LOCALE.empty_skills);
            renderSimpleList('equipped-items-list', getAt(stat, 'Mainchar.Equipment', {}), LOCALE.empty_equipped);
            renderGridList('inventory-list', getAt(stat, 'Mainchar.Inventory', {}), LOCALE.empty_inventory);
            renderSimpleList('talents-list', getAt(stat, 'Mainchar.Talents', {}), LOCALE.empty_talents);
            renderSimpleList('companions-list', getAt(stat, 'Mainchar.Companions', {}), LOCALE.empty_companions);
            renderSimpleList('real-estate-list', getAt(stat, 'Mainchar.Real_estate', {}), LOCALE.empty_real_estate);
            renderSimpleList('buffs-list', getAt(stat, 'Mainchar.Buffs', {}), LOCALE.empty_buffs);
            renderSimpleList('ailments-list', getAt(stat, 'Mainchar.Ailments', {}));
            renderSimpleList('mods-list', getAt(stat, 'Mainchar.Mods', {}), LOCALE.empty_mods);

            renderKinList('kin-container', getAt(stat, 'Familiar', {}));

            // ---- Map Logic ----
            const mcLevel = Number(getV(stat, 'Mainchar.Level', 0)) || 0;
            const mapContainer = document.getElementById('map-container');

            if (mapContainer) {
                let mapUrl = '';
                const mapWiKey = mcLevel < 71 ? 'pre-70-map' : 'post-70-map';

                try {
                    // API Discovery: Check Global (Script Runner) then Parent (ST Window)
                    const _getPrimaryBook = (typeof getCurrentCharPrimaryLorebook === 'function')
                        ? getCurrentCharPrimaryLorebook
                        : (typeof window.parent !== 'undefined' && window.parent.getCurrentCharPrimaryLorebook);

                    const _getEntries = (typeof getLorebookEntries === 'function')
                        ? getLorebookEntries
                        : (typeof window.parent !== 'undefined' && window.parent.getLorebookEntries);

                    if (typeof _getPrimaryBook === 'function' && typeof _getEntries === 'function') {
                        const bookName = await _getPrimaryBook();
                        if (bookName) {
                            const entries = await _getEntries(bookName);
                            if (Array.isArray(entries)) {
                                // Search: Check 'keys' array, legacy 'key', or 'comment' field.
                                // Case-insensitive matching.
                                const entry = entries.find(e => {
                                    const keys = e.keys || e.key;
                                    const hasKey = Array.isArray(keys) && keys.some(k => k.trim().toLowerCase() === mapWiKey.toLowerCase());
                                    const hasComment = e.comment && e.comment.trim().toLowerCase() === mapWiKey.toLowerCase();
                                    return hasKey || hasComment;
                                });

                                if (entry) {
                                    mapUrl = entry.content;
                                }
                            }
                        }
                    }


                } catch (e) {
                    console.error("Error fetching map:", e);
                }

                if (mapUrl && typeof mapUrl === 'string') {
                    const trimmed = mapUrl.trim();
                    if (trimmed && /^https?:\/\//i.test(trimmed)) {
                        mapContainer.innerHTML = `<img src="${trimmed}" style="width:100%; height:auto; object-fit:contain; border-radius:4px; box-shadow:0 0 5px rgba(0,0,0,0.5);" alt="Map" onclick="showImageModal('Map', '${trimmed}')" style="cursor:pointer">`;
                        if (mapContainer.querySelector('img')) mapContainer.querySelector('img').style.cursor = 'pointer';
                    } else {
                        mapContainer.innerHTML = `<span style="font-size:0.8em; color:var(--text-secondary);">(Invalid URL)</span>`;
                    }
                } else {
                    mapContainer.innerHTML = `<div style="display:flex; flex-direction:column; gap:5px;">
                        <span style="font-size:0.8em; color:var(--text-secondary);">(No Map Data)</span>
                     </div>`;
                }
            }
        }




        async function applyLocalization() {
            // Tabs
            setText('btn-tab-overview', LOCALE.tab_overview);
