            setText('btn-tab-inventory', LOCALE.tab_inventory);
            setText('btn-tab-social', LOCALE.tab_social);

            // Headers
            setText('header-world', LOCALE.header_world);
            setText('header-core', LOCALE.header_core);
            setText('header-quest', LOCALE.header_quest);
            setText('header-vitals', LOCALE.header_vitals);
            setText('header-combat', LOCALE.header_combat);
            setText('header-skills', LOCALE.header_skills);
            setText('header-equipped', LOCALE.header_equipped);
            setText('header-inventory', LOCALE.header_inventory);
            setText('header-talents', LOCALE.header_talents);
            setText('header-real-estate', LOCALE.header_real_estate);
            setText('header-status-assist', LOCALE.header_status_assist);
            setText('header-map', (LOCALE.header_map) ? LOCALE.header_map : "Location Map");

            // Modal UI
            setText('ui-modal-title', LOCALE.ui_select_portrait);
            setText('ui-btn-url', LOCALE.ui_url_btn);
            setText('ui-help-url', LOCALE.ui_url_help);
            setText('ui-hint-url', LOCALE.ui_url_hint);
        }

        document.addEventListener('DOMContentLoaded', async () => {
            await applyLocalization();
            try {
                const closeBtn = document.getElementById('portrait-mode-close-btn');
                if (closeBtn) closeBtn.addEventListener('click', closePortraitModeModal);
                const modal = document.getElementById('portrait-mode-modal');
                if (modal) {
                    modal.addEventListener('click', (e) => { if (e.target === modal) closePortraitModeModal(); });
                    modal.querySelectorAll('[data-portrait-mode]').forEach(btn => {
                        btn.addEventListener('click', (e) => {
                            e.preventDefault(); e.stopPropagation();
                            const raw = prompt(LOCALE.ui_prompt_url, '');
                            if (raw === null) return;
                            applyPortraitUpdate('url', raw);
                        });
                    });
                }
                const messages = await getChatMessages(getCurrentMessageId());
                const gameData = messages.length > 0 ? messages[0].data : {};
                await updateStatusPanel(gameData);
            } catch (error) { console.error(error); }
        });

        // MVU polling refresh (no events) + redraw only on change
        let _lastStatData = null;

        const refreshFromMvu = async () => {
            const modal = document.getElementById("portrait-mode-modal");
            if (modal && modal.style.display === "flex") return false;
            let latestStatData = null;

            // 1. Try to get data from variables (Live State)
            const targetWindow = (typeof window.getVariables === "function" && window) || (window.parent && typeof window.parent.getVariables === "function" && window.parent) || null;

            if (targetWindow) {
                const msgId = (typeof getCurrentMessageId === "function") ? getCurrentMessageId() : -1;
                const context = { type: 'message', message_id: msgId };

                // Try to get variables with context (matches testsample.html behavior)
                const vars = targetWindow.getVariables(context);
                if (vars && vars.stat_data) {
                    latestStatData = vars.stat_data;
                }
                // Fallback: Try without context
                else {
                    const globalVars = targetWindow.getVariables();
                    if (globalVars && globalVars.stat_data) {
                        latestStatData = globalVars.stat_data;
                    }
                }
            }

            // 2. Fallback: Try to get data from the specific chat message (Persisted State)
            if (!latestStatData && typeof getCurrentMessageId === "function") {
                const msgId = getCurrentMessageId();
                if (msgId) {
                    try {
                        const messages = await getChatMessages(msgId);
                        const msgStat = messages?.[0]?.data?.stat_data;
                        if (msgStat) latestStatData = msgStat;
                    } catch (e) { console.warn("getChatMessages failed", e); }
                }
            }

            // Change Detection & Update to avoid redraw
            if (latestStatData) {
                // Use Lodash isEqual if available, otherwise simple JSON stringify
                const isDifferent = (typeof _ !== 'undefined' && _.isEqual)
                    ? !_lastStatData || !_.isEqual(latestStatData, _lastStatData)
                    : JSON.stringify(latestStatData) !== JSON.stringify(_lastStatData);

                if (isDifferent) {
                    console.log("Data changed, refreshing UI...", { old: _lastStatData, new: latestStatData });
                    await updateStatusPanel({ stat_data: latestStatData });

                    // Clone data for next comparison to avoid reference issues
                    if (typeof _ !== 'undefined' && _.cloneDeep) {
                        _lastStatData = _.cloneDeep(latestStatData);
                    } else {
                        _lastStatData = JSON.parse(JSON.stringify(latestStatData));
                    }
                    return true;
                }
            } else {
                console.log("MVU Debug: No data found");
            }
            return false;
        };
        refreshFromMvu().catch(() => { });
        setInterval(() => { refreshFromMvu().catch(() => { }); }, 2000);
    </script>
</body>

</html>
