// --- ÂÖ®Â±ÄÈÖçÁΩÆ ---
const SCRIPT_NAME = 'ÈÄöÁî®ËßíËâ≤Êà∞È¨•Êï∏ÂÄºÁõ£Ë≠∑ËÖ≥Êú¨';
const SCRIPT_VERSION = 'v1.3';
// const CHARACTER_NAME = 'ÂüÉËéâË•øÂ©≠'; // Â∑≤Âª¢Ê£ÑÔºåËÖ≥Êú¨ÁèæÂú®ÊòØÈÄöÁî®ÁöÑ

// --- ‰∏ªËôïÁêÜÂáΩÊï∏ (Ë™øÂ∫¶‰∏≠ÂøÉ) ---
function onVariableUpdateEnded(variables) {
    // [v1.4] ‰øÆÊ≠£Êï∏ÊìöÁµêÊßãÔºåÊåáÂêëÊ≠£Á¢∫ÁöÑ stat_data Â∞çË±°
    variables = variables?.stat_data;

    // Á¢∫‰øùËßíËâ≤Ê†∏ÂøÉÊï∏ÊìöÂ≠òÂú®Ôºå‰ΩøÂÖ∂ÈÅ©Áî®Êñº‰ªª‰ΩïËßíËâ≤
    if (!variables?.Ê†∏ÂøÉÁãÄÊÖã || !variables?.ÂêçÂ≠ó?.[0]) {
        // console.log(`[${SCRIPT_NAME}] Êú™ÊâæÂà∞Ê†∏ÂøÉÊï∏ÊìöÊàñËßíËâ≤ÂêçÔºåË∑≥ÈÅéÁõ£Ë≠∑„ÄÇ`);
