    Ruins: z.record(z.string(), worldRecordEntrySchema).prefault({}),
    Events: z.record(z.string(), worldRecordEntrySchema).prefault({}),
  })
  .passthrough()
  .prefault({});

// -------------------------
// World (all labeled tuples)
// -------------------------
const worldSchema = z
  .object({
    Date: labeledStr,
    Time: labeledStr,
    Location: labeledValue,
    Weather: labeledStr,
  })
  .passthrough()
  .prefault({});

// -------------------------
// Mainchar (Flattened & Unified)
// -------------------------
const maincharSchema = z
  .object({
    Name: labeledStr,
    Image: labeledStr, // Added Image
    Race: labeledStr,
	Occupation: labeledStr,
    Level: labeledNum,
    Exp: labeledNum,
