    Mental_state: labeledStr,

    // Attributes (Standardized)
    Strength: labeledNum,
    Agility: labeledNum,
    Constitution: labeledNum,
    Intelligence: labeledNum,
    Wisdom: labeledNum,
    Charisma: labeledNum,

