  })
  .catchall(labeledValue) // Allow extra labeled fields if needed
  .prefault({});

// -------------------------
// Familiar member (Standardized)
// -------------------------
const familiarMemberSchema = z
  .object({
    Name: labeledStr,
    Race: labeledStr,
    Identity: labeledStr,
    Occupation: labeledStr,
    Level: labeledNum,
    Exp: labeledNum,
    'Core-points': labeledNum,
