    Hp_curr: labeledNum,
    Hp_max: labeledNum,
    Mp_curr: labeledNum,
    Mp_max: labeledNum,
    Sta_curr: labeledNum,
    Sta_max: labeledNum,

    // Attributes (Renamed from Attr_*)
    Strength: labeledNum,
    Agility: labeledNum,
    Constitution: labeledNum,
    Intelligence: labeledNum,
    Wisdom: labeledNum,
    Charisma: labeledNum, // Renamed from Attr_charisma

    // Combat Stats
    Physical_attack: labeledNum,
    Physical_defense: labeledNum,
    Magic_attack: labeledNum,
    Magic_defense: labeledNum,
    Magic_assist: labeledNum,

    Affection: labeledNum,
    Lewdness: labeledNum,
    Control_desire: labeledNum,
    M_level: labeledNum,

