    ExSkill: labeledStr,
    Biography: labeledStr,
    Equipment: z.record(z.string(), z.any()).prefault({}),
  })
  .catchall(labeledValue) // allow new labeled fields without breaking validation
  .prefault({});

const familiarSchema = z.record(z.string(), familiarMemberSchema).prefault({});

// -------------------------
